mapper.select:
SELECT ID, NAME,PHONE,EMAIL,STATE,TYPE
FROM NEW_MEMBER

	AND NAME LIKE '%'||#{name}||'%'
	AND STATE = #{state}
	AND TYPE = #{type}

SELECT ID, NAME, PASSWORD, PHONE, EMAIL, BANK_NAME, ACCOUNT_NUM, STATE, TYPE, ADDRESS, DETAIL_ADDRESS, ZIP_CODE 
FROM NEW_MEMBER
WHERE ID = #{id}

mapper.update:
UPDATE NEW_MEMBER
NAME = #{name}, PASSWORD = #{password}, EMAIL = #{email}, BANK_NAME = #{bankName}, PHONE = #{phone}, ACCOUNT_NUM = #{accountNum}, ADDRESS = #{address}, DETAIL_ADDRESS = #{detailAddress}, ZIP_CODE = #{zipCode}
WHERE ID = #{id}
